mapper.select:
SELECT * FROM PRODUCT P LEFT JOIN (SELECT CONCAT(A1.group_ID, A1.CATEGORY_ID) AS CATEGORY_KEY, ##카테고리 키 (그룹 + 키 형태로 카테고리분류) CASE A1.category_parent_LV WHEN 0 THEN A1.category_detail_NM WHEN 1 THEN CONCAT(A2.category_detail_NM,' > ', A1.category_detail_NM) WHEN 2 THEN CONCAT(A3.category_detail_NM, ' > ', A2.category_detail_NM,' > ', A1.category_detail_NM) END as CATEGORY_NAV FROM PRODUCT_CATEGORY A1 LEFT OUTER JOIN PRODUCT_CATEGORY A2 ON A1.CATEGORY_PARENT_LV = A2.CATEGORY_LV AND A1.CATEGORY_DETAIL_PARENT_LV = A2.CATEGORY_DETAIL_LV AND A1.GROUP_ID = A2.GROUP_ID LEFT OUTER JOIN PRODUCT_CATEGORY A3 ON A2.CATEGORY_PARENT_LV = A3.CATEGORY_LV AND A2.CATEGORY_DETAIL_PARENT_LV = A3.CATEGORY_DETAIL_LV ORDER BY CATEGORY_NAV ASC) X ON P.PROD_CATEGORY = X.CATEGORY_KEY WHERE PROD_WRITER = #{prodWriter}
AND PROD_NAME LIKE CONCAT('%', #{cri.searchName}, '%')
AND PROD_CONTENT LIKE CONCAT('%', #{cri.searchContent}, '%')
AND PROD_ENDDATE >= #{cri.startDate}
AND PROD_ENDDATE <= #{cri.endDate}
ORDER BY
PROD_PRICE ASC,
PROD_PRICE DESC,
PROD_ID DESC LIMIT #{cri.pageStart}, #{cri.amount}

SELECT COUNT(*) AS total
FROM PRODUCT
WHERE PROD_WRITER = #{prodWriter}

    AND PROD_NAME LIKE CONCAT('%', #{cri.searchName}, '%')


    AND PROD_CONTENT LIKE CONCAT('%', #{cri.searchContent}, '%')



    AND PROD_ENDDATE >= #{cri.startDate}




    AND PROD_ENDDATE <= #{cri.endDate}

mapper.update:
UPDATE PRODUCT
SET PROD_ENDDATE = #{prodEnddate}
  ,PROD_NAME = #{prodName}
  ,PROD_PRICE = #{prodPrice}
  ,PROD_COUNT = #{prodCount}
  ,PROD_DISCOUNT = #{prodDiscount}
  ,PROD_PURCHASE_YN = #{prodPurchaseYn}
  ,PROD_CONTENT = #{prodContent}
  ,PROD_COMMENT = #{prodComment}
WHERE PROD_ID = #{prodId}

mapper.select:
SELECT *
FROM PRODUCT_CATEGORY
WHERE GROUP_ID = #{groupId}
AND CATEGORY_PARENT_LV = #{categoryLv}
AND CATEGORY_DETAIL_PARENT_LV = #{categoryDetailLv}
